import { createStore } from 'vuex'

export default createStore({
  state: {
    sidebarOpen: false,
  },
  getters: {
  },
  mutations: {
    toggleSidebar(state) {
      state.sidebarOpen = !state.sidebarOpen;
    },
  },
  actions: {
    async toggleSidebarAsync({ commit }) {
      // You can perform asynchronous operations here if needed
      commit('toggleSidebar');
    },
  },
  modules: {
  }
})
