import { defineComponent } from "vue";
import FullCalendar from "@fullcalendar/vue3";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";
import { INITIAL_EVENTS, createEventId } from "./event-utils";
export default defineComponent({
  components: {
    FullCalendar
  },
  data() {
    return {
      calendarOptions: {
        plugins: [dayGridPlugin, timeGridPlugin, interactionPlugin],
        headerToolbar: {
          left: "prev,next today",
          center: "title",
          right: "dayGridMonth,timeGridWeek,timeGridDay"
        },
        initialView: "dayGridMonth",
        initialEvents: INITIAL_EVENTS,
        editable: true,
        selectable: true,
        selectMirror: true,
        dayMaxEvents: true,
        weekends: true,
        select: this.handleDateSelect,
        eventClick: this.handleEventClick,
        eventsSet: this.handleEvents,
        customButtons: {
          addNoteButton: {
            text: 'Add Note',
            click: this.handleAddNoteButtonClick
          }
        },
        // Add the custom button to the header
        headerToolbar: {
          left: "prev,next today",
          center: "title",
          right: "dayGridMonth,timeGridWeek,timeGridDay,addNoteButton"
        }
      },
      currentEvents: []
    };
  },
  methods: {
    handleWeekendsToggle() {
      this.calendarOptions.weekends = !this.calendarOptions.weekends;
    },
    handleDateSelect(selectInfo) {
      let title = prompt("Please enter a new title for your event");
      let time = prompt("Please enter the event time (HH:mm)");
      let calendarApi = selectInfo.view.calendar;
      calendarApi.unselect();
      if (title && time) {
        let start = selectInfo.startStr + "T" + time;
        let end = selectInfo.endStr + "T" + time;
        calendarApi.addEvent({
          id: createEventId(),
          title,
          start,
          end,
          allDay: false
        });
      }
    },
    handleEventClick(clickInfo) {
      if (confirm(`Are you sure you want to delete the event '${clickInfo.event.title}'`)) {
        clickInfo.event.remove();
      }
    },
    handleEvents(events) {
      this.currentEvents = events;
    },
    handleAddNoteButtonClick() {
      // Handle the button click, e.g., show a modal to add a note
      alert("Add Note button clicked!");
    }
  }
});
